function [h, Error] = RK4_ErrorGraph(to, yo, desiredT)     hVal = 1;      Error = [];   h = [];   estimations = [];        n = desiredT ./ hVal;     h = [h; hVal];     [t w] = RK4(to, yo, hVal, n);     hVal = hVal ./ 10;     estimations = [estimations; w(end)];     Error = [Error; 1];           for i=2 : 10     n = desiredT ./ hVal;     h = [h; hVal];     [t w] = RK4(to, yo, hVal, n);     hVal = hVal ./ 10;     estimations = [estimations; w(end)];     errorVal = abs( (estimations(i) - estimations(i-1))) ./ (2.^4 - 1);     Error = [Error; errorVal];      endfor        scatter(h, Error);  set(gca,'yscale','log');        
endfunction
