%estimate the error associated with RK4 results%n is the total number of stepsee = [];h = [];for i=1:20  n = 10*i;[t w] = RK4System(0, [4 0], 5/n , n); %calculates w1 and w2 (using RK4) with a stepsize = size of interval / nn = 10*i;[t ww] = RK4System(0, [4 0], 5/n , n); %calculates ww1 and ww2 using 2*n or h/2ee = [ee; abs(w(end, 1) - ww(end,1)) / (2^4 - 1)   abs(w(end,2) - ww(end,2)) / (2^4 - 1) ]h = [h; 5/n]endforloglog(h,ee, "linewidth", 3)set(gca, "fontsize", 26)